mapper.select:
select * from orders

    and status = #{status}
    and user_id = #{userId}

order by order_time desc

select * from orders

    and status = #{status}
    and order_time => STR_TO_DATE(#{beginTime}, '%Y-%m-%dT%H:%i:%s')
    and order_time => STR_TO_DATE(#{endTime}, '%Y-%m-%dT%H:%i:%s')
    and phone = #{phone}
    and number = #{number}

order by order_time
